// Simple JS for nav, theme, portfolio filtering, and contact form
document.getElementById('menuBtn').addEventListener('click', function(){
  const nav = document.getElementById('navLinks');
  if(window.innerWidth <= 900){
    nav.classList.toggle('menu-open');
  }
});

function handleSubmit(e){
  e.preventDefault();
  const form = document.getElementById('contactForm');
  const msg = document.getElementById('formMsg');
  msg.textContent = 'Thanks! Message captured locally (demo).';
  form.reset();
}

document.addEventListener('DOMContentLoaded', () => {
  // Normalize icons/labels present in HTML
  const menuBtn = document.getElementById('menuBtn');
  if(menuBtn){ menuBtn.textContent = '☰'; menuBtn.setAttribute('aria-label','Open menu'); }

  // Theme init
  const root = document.documentElement;
  const btn = document.getElementById('themeToggle');
  const saved = localStorage.getItem('theme');
  const theme = saved || 'light';
  root.setAttribute('data-theme', theme);
  if(btn){ btn.textContent = theme === 'dark' ? '🌙' : '☀️'; }
  if(btn){
    btn.addEventListener('click', () => {
      const current = root.getAttribute('data-theme') === 'dark' ? 'dark' : 'light';
      const next = current === 'dark' ? 'light' : 'dark';
      root.setAttribute('data-theme', next);
      localStorage.setItem('theme', next);
      btn.textContent = next === 'dark' ? '🌙' : '☀️';
    });
  }

  const hero = document.querySelector('.hero');
  const p = hero?.querySelector('.hero-left p');
  const cta = hero?.querySelector('.cta');
  if(p && /Cloud Networking|production-grade/.test(p.textContent || '')){
    p.innerHTML = 'Automating cloud infrastructure and AI platforms across Azure, AWS, and GCP. <strong>Terraform • Ansible • Kubernetes • Jenkins • GitHub Actions</strong>';
  }
  if(cta){
    const [a,b] = cta.querySelectorAll('a');
    if(a){ a.textContent = 'Explore My Work'; a.classList.add('neu'); a.setAttribute('href', '#portfolio'); }
    if(b){ b.textContent = 'Contact Me'; b.classList.add('neu','ghost'); b.setAttribute('href','#contact'); b.removeAttribute('target'); b.removeAttribute('rel'); }
  }

  // Reveal on scroll
  const io = new IntersectionObserver((entries)=>{
    entries.forEach(e=>{ if(e.isIntersecting) e.target.classList.add('in'); });
  }, { threshold: 0.2 });
  document.querySelectorAll('.reveal, .neu-outset').forEach(el=> io.observe(el));

  // Simple slider
  const slider = document.querySelector('.slider');
  if(slider){
    const slides = Array.from(slider.querySelectorAll('.slide'));
    let idx = 0;
    const show = (i)=>{ slides.forEach((s,j)=> s.classList.toggle('active', j===i)); };
    slider.querySelector('.prev')?.addEventListener('click', ()=>{ idx=(idx-1+slides.length)%slides.length; show(idx); });
    slider.querySelector('.next')?.addEventListener('click', ()=>{ idx=(idx+1)%slides.length; show(idx); });
    setInterval(()=>{ idx=(idx+1)%slides.length; show(idx); }, 6000);
  }

  // Parallax-ish hero background
  const bg = document.querySelector('.hero-bg');
  if(bg){
    window.addEventListener('scroll', ()=>{
      const y = window.scrollY * 0.15;
      bg.style.transform = `translateY(${y}px)`;
    });
  }

  // Fix encoding artifacts like replacement chars in meta bullets
  document.querySelectorAll('.meta').forEach(el=>{
    el.textContent = el.textContent.replace(/�/g, '•');
  });
  const heroP = document.querySelector('.hero-left p');
  if(heroP){ heroP.innerHTML = heroP.innerHTML.replace(/�/g, '•'); }

  // Normalize hero info icons
  const heroInfo = document.querySelectorAll('.hero-info li');
  if(heroInfo[0]) heroInfo[0].textContent = '✉️ shaikalthafcgl@gmail.com';
  if(heroInfo[1]) heroInfo[1].textContent = '📍 India';

  // Footer cleanup
  const footerP = document.querySelector('.footer .container p');
  if(footerP){
    let t = footerP.textContent;
    t = t.replace(/^c\s/, '© ').replace(/\?\?/g, '❤️').replace(/�/g, '•');
    footerP.textContent = t;
  }

  // Build enhanced Portfolio with filters
  const portfolio = document.getElementById('portfolio');
  const mason = document.getElementById('portfolioMasonry') || portfolio?.querySelector('.masonry');
  if(portfolio && mason){
    // Inject filters bar if missing
    if(!document.getElementById('portfolioFilters')){
      const filters = document.createElement('div');
      filters.className = 'filters';
      filters.id = 'portfolioFilters';
      filters.setAttribute('role','tablist');
      filters.innerHTML = `
        <button class="filter active" data-filter="all" role="tab" aria-selected="true">All</button>
        <button class="filter" data-filter="devops" role="tab">DevOps</button>
        <button class="filter" data-filter="cloud" role="tab">Cloud</button>
        <button class="filter" data-filter="ai" role="tab">AI</button>
        <button class="filter" data-filter="software" role="tab">Software</button>
      `;
      portfolio.insertBefore(filters, mason);
    }

    // Replace placeholder items with richer cards
    mason.innerHTML = '';
    const items = [
      {img:'assets/img/ai.svg', alt:'AI Cloud Automation', title:'AI Cloud Automation', desc:'End-to-end AI workload deployments with CI/CD, observability, and guardrails.', meta:'Azure • Kubernetes • Jenkins • Terraform', tags:['ai','cloud','devops']},
      {img:'assets/img/cloud.svg', alt:'Palo Alto Automation', title:'Palo Alto Automation', desc:'NGFW automation for DMZ/NAT with ARM, Bash, and Azure Load Balancer.', meta:'ARM • Bash • Azure', tags:['cloud','network','devops']},
      {img:'assets/img/software.svg', alt:'MacroCloud UI', title:'MacroCloud UI', desc:'Multi-cloud console UI for migration progress, auth, and incident views.', meta:'React • Tailwind • Azure', tags:['software','cloud']},
      {img:'assets/img/devops.svg', alt:'IaC Foundation', title:'IaC Foundation', desc:'Terraform/Ansible blueprints for reproducible, secure cloud infrastructure at scale.', meta:'Terraform • Ansible • GitHub Actions', tags:['devops','cloud']},
      {img:'assets/img/ai.svg', alt:'MLOps on AKS', title:'MLOps on AKS', desc:'Model training/serving with pipelines, model registry, and autoscaling on AKS.', meta:'AKS • MLflow • Argo', tags:['ai','cloud','devops']},
      {img:'assets/img/software.svg', alt:'Serverless APIs', title:'Serverless APIs', desc:'Event-driven APIs for telemetry ingestion and alerting with least-privilege design.', meta:'Azure Functions • Event Grid • Bicep', tags:['software','cloud']}
    ];
    items.forEach(it=>{
      const art = document.createElement('article');
      art.className = 'mason card neu-outset reveal';
      art.dataset.tags = it.tags.join(',');
      art.innerHTML = `
        <img class="mason-img" src="${it.img}" alt="${it.alt} thumbnail" loading="lazy" />
        <h4>${it.title}</h4>
        <p>${it.desc}</p>
        <p class="meta">${it.meta}</p>
      `;
      mason.appendChild(art);
    });

    // Filter logic
    const filterBar = document.getElementById('portfolioFilters');
    const cards = () => Array.from(mason.querySelectorAll('article'));
    const setFilter = (key) => {
      cards().forEach(card => {
        const tags = (card.dataset.tags || '').split(',');
        const show = key === 'all' || tags.includes(key);
        card.style.display = show ? 'inline-block' : 'none';
      });
    };
    filterBar.addEventListener('click', (e)=>{
      const btn = e.target.closest('.filter');
      if(!btn) return;
      filterBar.querySelectorAll('.filter').forEach(b=>{ b.classList.toggle('active', b===btn); b.setAttribute('aria-selected', b===btn ? 'true':'false'); });
      setFilter(btn.dataset.filter);
    });
    setFilter('all');
  }
});

styles.css.content:
:root{
  /* Light theme defaults */
  --text:#0b1220;
  --muted:#475569;
  --accent:#5ea8ea; /* Azure Blue accent */
  --bg-surface:#f8fafc;
  --card:#ffffff;
  --glass: rgba(0,0,0,0.03);
  --border: rgba(0,0,0,0.08);
  --bg-gradient: linear-gradient(180deg,#f6f9fc 0%, #eef2f7 100%);
  --container: 1100px;
  font-family: 'Inter', system-ui, -apple-system, 'Segoe UI', Roboto, 'Helvetica Neue', Arial;
}

[data-theme="dark"]{
  --text:#e6eef6;
  --muted:#9aa4b2;
  --accent:#5ea8ea;
  --bg-surface:#0f1724;
  --card:#0b1220;
  --glass: rgba(255,255,255,0.03);
  --border: rgba(255,255,255,0.03);
  --bg-gradient: linear-gradient(180deg,#071021 0%, #0b1220 100%);
}

*{box-sizing:border-box}
html,body{height:100%;margin-top: 60px;;background:var(--bg-gradient);color:var(--text)}
.container{max-width:var(--container);margin:0 auto;padding:1rem}

/* Nav */
.nav{position:fixed;top:0;left:0;right:0;z-index:1000;background:linear-gradient(180deg,rgba(255,255,255,0.55),rgba(209,210,211,0.35));border-bottom:1px solid rgba(255,255,255,0.03);backdrop-filter:blur(6px)}
.nav-inner{display:flex;justify-content:space-between;align-items:center}
.brand{font-weight:700;color:var(--accent);text-decoration:none;display:flex;align-items:center}
.brand img{width:32px;height:32px;border-radius:50%;margin-right:8px}
.nav-links{list-style:none;display:flex;gap:1rem;margin:0;padding:0}
.nav-links a{color:var(--muted);text-decoration:none;font-weight:600}
.menu-btn{display:none;background:none;border:0;color:var(--muted);font-size:1.4rem}
.theme-toggle{background:transparent;border:1px solid var(--border);color:var(--muted);width:34px;height:34px;border-radius:50%;display:grid;place-items:center;cursor:pointer}
.theme-toggle:hover{box-shadow:0 0 0 2px rgba(94,168,234,0.18) inset;color:var(--text)}

/* Hero */
.hero{padding:3rem 0;position:relative;overflow:hidden}
.hero-bg{position:absolute;inset:0;background:
  radial-gradient(800px 400px at 10% 20%, rgba(94,168,234,0.15), transparent 60%),
  radial-gradient(600px 300px at 90% 40%, rgba(94,234,212,0.10), transparent 60%);
  filter: blur(12px);
  pointer-events:none;}
.hero-inner{display:flex;gap:2rem;align-items:center}
.hero-left{flex:1}
.hero-right{width:260px;position:relative}
.avatar{width:200px;border-radius:12px;border:4px solid rgba(255,255,255,0.04);display:block}
.avatar.round{border-radius:50%}
h1{margin:0;font-size:2rem}
h2{margin:0.3rem 0 1rem;color:var(--muted);font-weight:600}
.accent{color:var(--accent)}
p{color:var(--muted);line-height:1.5}
.cta{margin-top:1rem;display:flex;gap:0.6rem}
.btn{background:var(--accent);color:#04202a;padding:0.6rem 0.9rem;border-radius:12px;text-decoration:none;font-weight:700}
.btn.ghost{background:transparent;border:1px solid rgba(255,255,255,0.05);color:var(--muted)}
.btn.neu{background:var(--bg-surface);border-radius:14px;box-shadow:
  8px 8px 16px rgba(0,0,0,0.15),
  -8px -8px 16px rgba(255,255,255,0.6);
  border:1px solid var(--border);color:var(--text)}
.btn.neu.ghost{background:var(--bg-surface);border:1px solid var(--border);color:var(--muted)}
.btn.neu:hover{box-shadow:
  4px 4px 10px rgba(0,0,0,0.18),
  -4px -4px 10px rgba(255,255,255,0.5),
  0 0 0 2px rgba(94,168,234,0.15) inset}
.titles{margin:0.4rem 0 1rem}
.title-lg{font-size:1.2rem;font-weight:700}
.title-md{font-size:1rem;color:var(--muted);font-weight:600}
.title-sm{font-size:0.95rem;color:var(--muted)}

.logo-badge {
  width: 44px;
  height: 44px;
  border-radius: 50%;
  background: white;
  border: 1px solid var(--border);
  display: inline-grid;
  place-items: center;
  box-shadow: 4px 4px 8px rgba(0,0,0,0.08), -4px -4px 8px rgba(255,255,255,0.5);
  margin-right: 0.6rem;
}

.logo-badge img {
  width: 36x;
  height: 36px;
  object-fit: contain;
}

/* sections */
.section{padding:2.4rem 0;border-top:1px solid rgba(255,255,255,0.02)}
.grid{display:grid;grid-template-columns:repeat(auto-fit,minmax(220px,1fr));gap:1rem}
.card{background:var(--glass);padding:1rem;border-radius:10px;border:1px solid var(--border)}
.card h4{margin:0 0 0.5rem}
.card h4{display:flex;align-items:center;gap:0.6rem}
.icon-badge{width:44px;height:44px;border-radius:50%;background:var(--card);border:1px solid var(--border);display:inline-grid;place-items:center;box-shadow:4px 4px 8px rgba(0,0,0,0.08), -4px -4px 8px rgba(255,255,255,0.5)}
.icon-badge .fa-solid{font-size:18px;color:#f1b43b}
.meta{color:var(--muted);font-size:0.9rem}
.links a{color:var(--accent);text-decoration:none}

/* Neumorphism helpers */
.neu-outset{background:var(--bg-surface);border-radius:14px;border:1px solid var(--border);box-shadow:
  10px 10px 20px rgba(0,0,0,0.12),
  -10px -10px 20px rgba(255,255,255,0.6)}
.neu-inset{background:var(--bg-surface);border-radius:14px;box-shadow:
  inset 10px 10px 20px rgba(0,0,0,0.15),
  inset -10px -10px 20px rgba(255,255,255,0.5)}

/* About cards */
.about-grid{display:grid;grid-template-columns:repeat(auto-fit,minmax(240px,1fr));gap:1rem}
.about-notes .muted{color:var(--muted)}

/* Founder Journey */
.founder-strip{display:flex;gap:1rem;overflow-x:auto;padding-bottom:0.5rem;scroll-snap-type:x mandatory}
.founder-card{min-width:280px;scroll-snap-align:center;padding:1rem;border-radius:14px;border:1px solid rgba(255,255,255,0.04)}
.logo-circle{width:86px;height:86px;border-radius:50%;display:grid;place-items:center;background:rgba(255,255,255,0.04);border:1px solid rgba(255,255,255,0.06);margin-bottom:0.6rem}
.logo-circle img{width:64px;height:64px;object-fit:contain;display:block;border-radius:6px}
.macrocloud-logo{height:66px;width:60%;}
.founder-dots{display:flex;gap:6px;justify-content:center;margin-top:0.6rem}
.founder-dots .dot{width:6px;height:6px;border-radius:50%;background:rgba(255,255,255,0.15)}
.founder-dots .dot.active{background:var(--accent)}

/* Masonry Portfolio */
.masonry{column-count:3;column-gap:1rem}
.mason{display:inline-block;width:100%;margin:0 0 1rem;break-inside:avoid}
.mason-img{display:block;width:100%;height:auto;border-radius:10px;margin-bottom:0.6rem;background:linear-gradient(180deg, rgba(255,255,255,0.04), rgba(255,255,255,0.02))}
.mason-img.placeholder{height:140px}
.mason-img.placeholder.tall{height:220px}
.mason-img.placeholder.wide{height:160px}

/* Portfolio filters */
.filters{display:flex;flex-wrap:wrap;gap:0.5rem;margin:0.6rem 0 1rem}
.filter{background:var(--bg-surface);border:1px solid var(--border);color:var(--muted);padding:0.4rem 0.7rem;border-radius:999px;cursor:pointer;font-weight:600}
.filter.active{color:var(--text);box-shadow:0 0 0 2px rgba(94,168,234,0.18) inset}

/* Slider */
.slider{position:relative;padding:1rem;border-radius:14px;border:1px solid rgba(255,255,255,0.04)}
.slides{position:relative;min-height:120px}
.slide{position:absolute;inset:0;opacity:0;transition:opacity .5s ease}
.slide.active{opacity:1}
.slide-btn{position:absolute;top:50%;transform:translateY(-50%);background:transparent;color:var(--muted);border:0;font-size:1.6rem;cursor:pointer}
.slide-btn.prev{left:0.4rem}
.slide-btn.next{right:0.4rem}

/* bullets + timeline */
.bullets{margin:0.6rem 0 0.2rem 0.9rem;color:var(--muted)}
.bullets li{margin:0.25rem 0}
.timeline{display:grid;gap:1rem}
.timeline-item{display:grid;grid-template-columns:80px 1fr;gap:1rem;align-items:flex-start}
.tl-left{display:flex;flex-direction:column;align-items:center}
.tl-dot{width:64px;height:64px;border-radius:50%;background:var(--card);border:1px solid var(--border);display:grid;place-items:center;box-shadow: 6px 6px 12px rgba(0,0,0,0.1), -6px -6px 12px rgba(255,255,255,0.5)}
.tl-dot .fa-solid{font-size:22px;color:#f1b43b}
.tl-line{flex:1;width:2px;background:var(--border);margin-top:8px}

/* skills */
.skills-grid{display:flex;flex-wrap:wrap;gap:0.6rem}
.skill{background:linear-gradient(180deg, rgba(255,255,255,0.02), rgba(255,255,255,0.01));padding:0.5rem 0.8rem;border-radius:8px;border:1px solid rgba(255,255,255,0.02);font-weight:600}

/* contact form */
.contact-form{max-width:640px;display:flex;flex-direction:column;gap:0.6rem}
.contact-form label{display:flex;flex-direction:column;color:var(--muted);font-weight:600}
.contact-form input, .contact-form textarea{padding:0.8rem 0.9rem;border-radius:12px;border:1px solid var(--border);background:var(--bg-surface);color:inherit;box-shadow: inset 6px 6px 12px rgba(0,0,0,0.08), inset -6px -6px 12px rgba(255,255,255,0.5)}
.form-msg{margin-top:0.4rem;color:var(--accent)}

/* footer */
.footer{padding:1.2rem 0;border-top:1px solid var(--border);text-align:center;color:var(--muted)}
.footer-inner{display:flex;align-items:center;justify-content:space-between;gap:1rem;flex-wrap:wrap}
.footer-nav a{color:var(--muted);text-decoration:none;margin:0 0.4rem}
.icon-link{display:inline-grid;place-items:center;width:28px;height:28px;border-radius:50%;border:1px solid var(--border);color:#4b5563;text-decoration:none}
.icon-link:hover{box-shadow:0 0 0 2px rgba(94,168,234,0.18) inset;color:#0f1724}
.footer-inner{display:flex;align-items:center;justify-content:space-between;gap:1rem;flex-wrap:wrap}
.footer-nav a{color:var(--muted);text-decoration:none;margin:0 0.4rem}
.icon-link{display:inline-grid;place-items:center;width:28px;height:28px;border-radius:50%;border:1px solid rgba(255,255,255,0.06);color:#cfd7e3;text-decoration:none}
.icon-link:hover{box-shadow:0 0 0 2px rgba(94,168,234,0.18) inset;color:#fff}

/* responsive */
@media (max-width:900px){
  .hero-inner{flex-direction:column-reverse;align-items:flex-start}
  .hero-right{width:100%}
  .nav-links{display:none}
  .menu-btn{display:block}
  .menu-open{display:flex;position:absolute;right:1rem;top:64px;background:var(--card);padding:0.6rem;border-radius:8px;flex-direction:column}
  .masonry{column-count:1}
}

/* hide deprecated sections if present */
#projects, #blog { display:none; }

/* simple reveal animations */
.reveal{opacity:0;transform:translateY(10px);transition:opacity .5s ease, transform .5s ease}
.reveal.in{opacity:1;transform:none}
